<!DOCTYPE html> <html lang="en"> <head> <meta charset="utf-8"> <title>Twitter Bootstrap Popover Example</title> <meta name="description" content="Creating Modal Window with Twitter Bootstrap"><link href="../styles/css/mod.bootstrap.css" rel="stylesheet"> <script src="../styles/js/jquery.js"></script><script src="../styles/js/bootstrap.min.js"></script><script>$(document).ready(function () {	$("#addButton").click(function () {		if( ($('.form-horizontal .control-group').length+1) > 3) {			alert("Only 3 control-group allowed");			return false;		}		var id = ($('.form-horizontal .control-group').length + 1).toString();		$('.form-horizontal').append('<div class="control-group" id="control-group' + id + '"> \										<label class="control-label" for="inputEmail' + id + '">Email' + id + '</label> \									  <div class="controls' + id + '"> \										<input type="text" id="inputEmail' + id + '" placeholder="Email' + id + '"> \									  </div> \									  </div>');	});	$("#removeButton").click(function () {		if ($('.form-horizontal .control-group').length == 1) {			alert("No more textbox to remove");			return false;		}		$(".form-horizontal .control-group:last").remove();	});});</script></head><body><!-- Modal --><div class="container">  <div class="form-horizontal">        <div class="control-group">            <label class="control-label" for="inputEmail">                Email</label>            <div class="controls">                <input type="text" id="inputEmail" placeholder="Email" />                </div>        </div>    </div>    <input type='button' value='Add Button' id='addButton' />    <input type='button' value='Remove Button' id='removeButton' />	</div><!--<script src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js"></script>--><!--<script src="/twitter-bootstrap/twitter-bootstrap-v2/js/bootstrap-tooltip.js"></script>--><!--<script src="/twitter-bootstrap/twitter-bootstrap-v2/js/bootstrap-popover.js"></script>--><script src="js/bootstrap.js"></script><script src="js/jquery.js"></script><script>$(function (){ $("#example").popover({placement:'right'});});</script></body></html>            